def longestPalindrome(s: str) -> str:
    """поиск наиболее длинной строки-палиндрома в строке"""
    len_s = len(s)
    for len_palindrom in range(len_s, 1, -1):
        middle = len_palindrom // 2
        for i in range(0, len_s - len_palindrom+1):
            left_middle = s[i:i+middle]
            right_middle = s[i+len_palindrom-1:i+len_palindrom-1-middle:-1]
            if left_middle == right_middle:
                return s[i:i+len_palindrom]

    return s[0]

print(longestPalindrome(''))
print(longestPalindrome(""))
print(longestPalindrome(""))
print(longestPalindrome(""))

print(longestPalindrome('babad'))

print(longestPalindrome('bbbbbbbbbbbb'))
print(longestPalindrome('cbbd'))
print(longestPalindrome('axzazxa'))
print(longestPalindrome('ccd'))




